#!/bin/usr/R
library(data.table)
library(dplyr)
library(corrplot)

argv = commandArgs(trailingOnly =T)

a = argv[1]
b = argv[2]
c = argv[3]
d = argv[4]
e = argv[5]

recode <- read.table(paste(a,c,sep=""),header=T)
bim <- read.table(paste(b,sep=""))
names(recode) <- bim$V2
LDmatrix <- cor(recode)
LDmatrix_LDscore <- LDmatrix*LDmatrix
Genetic_LDscore <- data.frame(rowSums(LDmatrix_LDscore))

write.table(Genetic_LDscore,paste(d,e,sep=""),quote=FALSE,sep="\t")
print("completed")
print(e)
